package desafio8;

public class Triangulo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1; i<=5;i++) {
			for(int j=1;j<=i;j++) {
			System.out.print(i);
			}
			System.out.print("\n");
		}
		
		System.out.println("este cambio fue hecho en github ");
	}

}
